mapper.select:
SELECT COUNT(*) 
FROM event_comment
WHERE event_key = #{eventKey}

mapper.update:
UPDATE event_comment
SET event_comment_content = #{eventCommentContent}
	, createdate = now()
WHERE event_comment_key = #{eventCommentKey}

mapper.insert:
INSERT INTO event_comment (
	event_comment_content
	, createdate
	, customer_id
	, schedule_key
	, event_key
	, insult_report
	, spoiler_report
) VALUES (
	#{eventCommentContent}
	, now()
	, #{customerId}
	, #{scheduleKey}
	, #{eventKey}
	, #{insultReport}
	, #{spoilerReport}
)
