mapper.update:
UPDATE eventImg
SET file_name = #{fileName}
	, file_type = #{fileType}
	, file_size = #{fileSize}
	, origin_name = #{originName}
WHERE event_key = #{eventKey}

mapper.insert:
INSERT INTO event_img (
	event_key
	, file_name
	, file_type
	, file_size
	, origin_name
	, createdate
) VALUES (
	#{eventKey}
	, #{fileName}
	, #{fileType}
	, #{fileSize}
	, #{originName}
	, now()
)
