mapper.update:
UPDATE question
SET question_title = #{questionTitle}, question_content = #{questionContent}
WHERE question_key = #{questionKey} AND customer_id = #{customerId}

mapper.insert:
INSERT INTO question (
	question_title
	, customer_id
	, question_category_key
	, theater_key
	, question_content
) VALUES (
	#{questionTitle}
	, #{customerId}
	, #{questionCategoryKey}
	, #{theaterKey}
	, #{questionContent}
)
